import React, { useState, useEffect } from 'react';
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid'
import Card from '@material-ui/core/Card'
import CardMedia from '@material-ui/core/CardMedia'
import CardContent from '@material-ui/core/CardContent'
import CardActions from '@material-ui/core/CardActions'
import Typography from '@material-ui/core/Typography';
import { Link } from '@material-ui/core';

export default function DiaryListSection() {
    const [diaries, setDiaries] = useState([])

    async function fetchDiaries() {
        const res = await fetch("https://salvagerapi.salvagerindonesia.com/api/v1/diaries?format=json")
        const diaries = await res.json()
        console.log(diaries)
        setDiaries(diaries)
    }

    var diaryList = diaries.map((diary) => {
        return <div key={diary.toString()}>
            <ItemDiary diary={diary} />
        </div>
    })

    useEffect(() => {
        fetchDiaries()
    }, [])

    const classes = useStyles();

    return (
        <Grid container>
            {diaryList}
        </Grid>
    )
}


const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    details: {
        display: 'flex',
        flexDirection: 'column',
    },
    content: {
        flex: '1 0 auto',
    },
}));

function ItemDiary(props) {
    const classes = useStyles();
    const diary = props.diary
    return (
        <div>
            <Grid xs={12}>
                <Grid container>
                    <Grid xs={12} sm={6}>
                        <img className={classes.diaryImage} src={diary.image} />
                    </Grid>
                    <Grid xs={12} sm={6}>
                        <Grid container spacing={2}>
                            <Grid xs={12}>
                                <Typography gutterBottom variant="headline" component="h1">
                                    {diary.headline}
                                </Typography>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
            {/* <Card className={classes.card} raised={false}>
                <CardMedia
                    className={classes.img}
                    style = {{ height: 0, paddingTop: '56%'}}
                    src={diary.image}
                />
                <CardContent className={classes.content}>
                    <Typography gutterBottom variant="headline" component="h1">
                        {diary.headline}
                    </Typography>
                    <Typography component="p">
                        {diary.content}
                    </Typography>
                </CardContent>
                <CardActions>
                </CardActions>
            </Card> */}
        </div >
    )
}
