import React, { useState, useEffect } from 'react';
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography'
import NavbarSection from '../NavbarSection'
import FooterSection from '../home/FooterSection';
import { Container } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import Grid from '@material-ui/core/Grid'

export default function ProductDetail() {
    const classes = useStyles();
    const { id } = useParams();
    const [product, setProduct] = useState([])

    async function fetchProduct() {
        const res = await fetch("https://salvagerapi.salvagerindonesia.com/api/v1/products/" + id + "?format=json")
        const product = await res.json()
        setProduct(product)
        console.log(product)
    }

    useEffect(() => {
        fetchProduct();
    }, []);

    return (
        <div>
            <Container>
                <NavbarSection theme='dark' />
                <Grid container>
                    <Grid item xs={12} sm={6} md={6}>
                        <img className={classes.diaryImage} src={diary.image} />
                    </Grid>
                    <Grid item xs={12} sm={6} md={6}>
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <div className={classes.diaryTextContainer}>
                                    <Typography gutterBottom variant="h4">
                                        {diary.headline}
                                    </Typography>
                                    <Typography gutterBottom variant="h6">
                                        By {diary.created_by}
                                    </Typography>
                                    <Divider />
                                    <Typography component="p">
                                        {diary.content}
                                    </Typography>
                                </div>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Container>
            <FooterSection />
        </div>
    )
}

const useStyles = makeStyles(theme => ({
    root: {
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    diaryTitle: {
        alignItems: 'center',
        marginTop: '1rem'
    }
}));
