import React, { useState, useEffect } from 'react';
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import StarComponent from '../StarComponent'
import Grid from '@material-ui/core/Grid'
import Typography from '@material-ui/core/Typography';
import { Link } from '@material-ui/core';
import { Divider } from 'antd';

export default function ReviewSection(props) {

    var reviewList = props.reviews.map((review) => {
        return <div key={review.toString()}>
            <Grid item xs={12}>
                <ItemReview review={review} />
            </Grid>
        </div>
    })

    const classes = useStyles();

    return (
        <Grid container spacing={1}>
            <Grid item xs={12}>
                <Typography variant='h4' align='center'>Customer Review</Typography>
            </Grid>
            {reviewList}
        </Grid>
    )
}


const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    details: {
        display: 'flex',
        flexDirection: 'column',
    },
    content: {
        flex: '1 0 auto',
    },
    reviewImage: {
    },
    reviewTextContainer: {
    },
}));

function ItemReview(props) {
    const classes = useStyles();
    const review = props.review
    return (
        <div className={classes.review}>
            <Grid container>
                <Grid item xs={12}>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <div className={classes.reviewTextContainer}>
                                <StarComponent star={5} />
                                <Typography gutterBottom variant="h5">
                                    {review.headline}
                                </Typography>
                                <Typography gutterBottom variant="p">
                                    from {review.username} on {review.pub_time.substring(0,10)}
                                </Typography>
                                <Typography component="p">
                                    {review.content}
                                </Typography>
                            </div>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </div >
    )
}
