import React, { useState, useEffect } from 'react';
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography'
import NavbarSection from '../NavbarSection'
import FooterSection from '../home/FooterSection';
import { Container } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';

export default function ProductDetail() {
    const classes = useStyles();
    const { id } = useParams();
    const [product, setProduct] = useState([])
    const [productImages, setProductImages] = useState([])

    async function fetchProduct() {
        const res = await fetch("https://salvagerapi.salvagerindonesia.com/api/v1/products/" + id + "?format=json")
        const product = await res.json()
        setProduct(product)
        setProductImages(product.product_images)
        console.log(product_images[0])
    }

    useEffect(() => {
        fetchProduct();
    }, []);

    // const product_images = product.product_images
    // console.log(product_images)
    return (
        <div>
            <Container>
                <NavbarSection theme='dark' />
                <Grid container>
                    <Grid item xs={12} sm={6} md={6}>
                        <img className={classes.productImage} src={product.product_images} />
                    </Grid>
                    <Grid item xs={12} sm={6} md={6}>
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <div className={classes.productTextContainer}>
                                    <Typography gutterBottom variant="h4">
                                        {product.name}
                                    </Typography>
                                    <Typography gutterBottom variant="h6">
                                        By {product.created_by}
                                    </Typography>
                                    <Divider />
                                    <Typography component="p">
                                        {product.content}
                                    </Typography>
                                </div>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Container>
            <FooterSection />
        </div>
    )
}

const useStyles = makeStyles(theme => ({
    root: {
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    productTitle: {
        alignItems: 'center',
        marginTop: '1rem'
    }
}));
