import React, { useState, useEffect } from 'react';
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid'
import Typography from '@material-ui/core/Typography';
import { Link } from '@material-ui/core';
import { Divider } from 'antd';

export default function ReviewSection(props) {

    var reviewList = props.reviews.map((review) => {
        return <div key={review.toString()}>
            <Grid>
                <ItemReview review={review} />
            </Grid>
        </div>
    })

    const classes = useStyles();

    return (
        <Grid container spacing={1}>
            <Grid item>
                <Typography variant='h4'>Customer Review</Typography>
            </Grid>
            {reviewList}
        </Grid>
    )
}


const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    details: {
        display: 'flex',
        flexDirection: 'column',
    },
    content: {
        flex: '1 0 auto',
    },
    reviewImage: {
        // height: '32rem',
        width: '100%',
        margin: '2rem',
        display: 'block',
        marginLeft: 'auto',
        marginRight: 'auto',
    },
    reviewTextContainer: {
        marginTop: '1rem',
        marginRight: '2rem',
        marginBottom: '2rem',
        marginLeft: '0rem',
        padding: '2rem'
    },
}));

function ItemReview(props) {
    const classes = useStyles();
    const review = props.review
    return (
        <div className={classes.review}>
            <Grid container>
                <Grid item xs={12} sm={6} md={6}>
                    <img className={classes.reviewImage} src={review.image} />
                </Grid>
                <Grid item xs={12} sm={6} md={6}>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <div className={classes.reviewTextContainer}>
                                <Typography gutterBottom variant="h4">
                                    {review.headline}
                                </Typography>
                                <Typography gutterBottom variant="h6">
                                    By {review.created_by}
                                </Typography>
                                <Divider />
                                <Typography component="p">
                                    {review.content}
                                </Typography>
                            </div>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </div >
    )
}
