import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
// import logo from './logo.svg';
// import './App.css';

import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
// import MenuIcon from '@material-ui/icons/Menu';
import BlackLogo from '../assets/img/black-logo.png';
import WhiteLogo from '../assets/img/white-logo.png';
import { Grid, Container } from '@material-ui/core';
import { Router, Route, Switch, Link } from 'react-router-dom';

import clsx from 'clsx';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import IconBurgerButton from '../assets/img/burgerBtn.png'
import IconBlackBurgerButton from '../assets/img/burgerBtnBlack.png'

import PropTypes from 'prop-types';
// import AppBar from '@material-ui/core/AppBar';
// import Toolbar from '@material-ui/core/Toolbar';
import CssBaseline from '@material-ui/core/CssBaseline';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Slide from '@material-ui/core/Slide';

let Color;

{/* <NavbarSection theme='light' /> */ }
export default function NavbarSection(props) {

    let isMobile = false;
    if (window.innerWidth >= 425) {
        isMobile = false;
        console.log('ini desktop')
    } else {
        isMobile = true;
        console.log('ini mobile')
    }
    console.log(window.innerWidth)

    const classes = useStyles();
    let Logo;
    let LogoMobile;
    let IconBurger;
    switch (props.theme) {
        case 'dark':
            Logo = BlackLogo;
            IconBurger = IconBlackBurgerButton;
            Color = 'black';
            break;
        case 'light':
            Logo = WhiteLogo;
            IconBurger = IconBurgerButton;
            Color = 'white';
            break;
    }
    if (props.logo === 'off') {
        Logo = '';
    }

    const [state, setState] = React.useState({
        top: false,
        left: false,
        bottom: false,
        right: false,
    });

    const toggleDrawer = (anchor, open) => event => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
            return;
        }

        setState({ ...state, [anchor]: open });
    };

    const list = anchor => (
        <div
            className={clsx(classes.list, {
                [classes.fullList]: anchor === 'top' || anchor === 'bottom',
            })}
            role="presentation"
            onClick={toggleDrawer(anchor, false)}
            onKeyDown={toggleDrawer(anchor, false)}
        >
            <List>
                <Link to='/' className={classes.linkMobile} color="inherit">
                    <ListItem>
                        <Button>HOME</Button>
                    </ListItem>
                </Link>
                <Divider />
                <Link to='/story' className={classes.linkMobile} color="inherit">
                    <ListItem>
                        <Button>STORY</Button>
                    </ListItem>
                </Link>
                <Divider />
                <Link to='/products' className={classes.linkMobile} color="inherit">
                    <ListItem>
                        <Button>PRODUCTS</Button>
                    </ListItem>
                </Link>
                <Divider />
                <Link to='/diary' className={classes.linkMobile} color="inherit">
                    <ListItem>
                        <Button>DIARY</Button>
                    </ListItem>
                </Link>
                <Divider />
                <Link to='/contact' className={classes.linkMobile} color="inherit">
                    <ListItem>
                        <Button>CONTACT</Button>
                    </ListItem>
                </Link>
            </List>
        </div>
    );


    if (isMobile) {
        return (
            <div>
                <HideOnScroll {...props}>
                    <AppBar elevation={0} className={classes.bgTransparent}>
                        <div position="static" className={classes.navbar}>

                            {['left'].map(anchor => (
                                <React.Fragment key={anchor}>
                                    <Button className={classes.mobileBurgerButton} onClick={toggleDrawer(anchor, true)}>
                                        {/* {anchor} */}
                                        <img src={IconBurger} />
                                    </Button>
                                    <Drawer anchor={anchor} open={state[anchor]} onClose={toggleDrawer(anchor, false)}>
                                        {list(anchor)}
                                    </Drawer>
                                </React.Fragment>
                            ))}
                            <img className={classes.mobileLogo} src={Logo} disply='inline' />
                        </div>
                    </AppBar>
                </HideOnScroll>
            </div>
        )
    } else {
        return (
            <div>
                <HideOnScroll {...props}>
                    <AppBar elevation={0} className={classes.bgTransparent}>
                        <div position="static" className={classes.navbar}>
                            <Container>
                                <Grid container>
                                    <Grid item xs={12} sm={6}>
                                        <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                                            <img className={classes.logo} src={Logo} />
                                        </IconButton>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <Toolbar>
                                            <Button><Link style={{ color: Color }} className={classes.link} to='/' color="inherit">HOME</Link></Button>
                                            <Button><Link style={{ color: Color }} className={classes.link} to='/story' color="inherit">STORY</Link></Button>
                                            <Button><Link style={{ color: Color }} className={classes.link} to='/products' color="inherit">PRODUCTS</Link></Button>
                                            <Button><Link style={{ color: Color }} className={classes.link} to='/diary' color="inherit">DIARY</Link></Button>
                                            <Button><Link style={{ color: Color }} className={classes.link} to='/contact' color="inherit">CONTACT</Link></Button>
                                        </Toolbar>
                                    </Grid>
                                </Grid>
                            </Container>
                        </div>
                    </AppBar>
                </HideOnScroll>
            </div>
        )
    }
}

const useStyles = makeStyles(theme => ({
    bgTransparent: {
        backgroundColor: 'transparent'
    },
    list: {
        width: 250,
    },
    fullList: {
        width: 'auto',
    },
    mobileLogo: {
        height: '3em',
        // width: '50%',
        marginLeft: '1em',
        marginTop: '1em',
    },
    mobileBurgerButton: {
        // marginLeft: '1em',
        marginTop: '1em',
    },

    navbar: {
        color: 'black',
        // height: 48,
        // padding: '0 30px',
        boxShadow: '0 3px 5px 2px edeeec',
        // boxShadow: 'none'
        backgroundColor: 'transparent',
    },
    menuButton: {
        marginRight: theme.spacing(2),
        width: '70%',
    },
    title: {
        flexGrow: 1,
    },
    background: {
        backgroundColor: 'transparent',
    },
    logo: {
        width: '100%'
    },
    link: {
        // color: Color,
        fontWeight: 'bold',
        margin: '1rem'
    },
    linkMobile: {
        color: 'black',
    }
}));

function HideOnScroll(props) {
    const { children, window } = props;
    // Note that you normally won't need to set the window ref as useScrollTrigger
    // will default to window.
    // This is only being set here because the demo is in an iframe.
    const trigger = useScrollTrigger({ target: window ? window() : undefined });

    return (
        <Slide appear={false} direction="down" in={!trigger}>
            {children}
        </Slide>
    );
}

HideOnScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};
