import React from 'react';
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid'
import Typography from '@material-ui/core/Typography';
import { Link } from '@material-ui/core';

export default class DiaryListSection extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            error: null,
            isLoaded: false,
            diaries: [

            ]
        };
    }

    componentDidMount() {
        fetch("https://salvagerapi.salvagerindonesia.com/api/v1/diaries")
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    items: result.items
                });
            },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                })
            }
        )
    }

    render() {
        const classes = useStyles();

        return (
            <div className={classes.firstDiv}>

            </div>
        )
    }
}

const useStyles = makeStyles(theme => ({

}));

function ItemDiary(props) {
    const classes = useStyles();
    const Star = props.star;
    let StarImg;
    switch (props.star) {
        case '5':
            StarImg = ImgStar5
            break;
        case '4':
            StarImg = ImgStar4
            break;
        case '3':
            StarImg = ImgStar3
            break;
    }
    return (
        <div>
            <Link to={props.link} className={classes.link} style={{ textDecoration: 'none' }}>
                <img className={classes.imgProducts} src={props.product} />
                <Typography variant="h4" gutterBottom align='center'>
                    {props.name} </Typography>
                <img className={classes.imgStar} src={StarImg} />
                <Typography variant="subtitle1" gutterBottom align='center'>
                    {props.price} </Typography>
            </Link>
        </div>
    )
}
